
    $(document).ready(function () {
      // Function to get the value of a URL parameter
      $.urlParam = function (name) {
        var results = new RegExp('[?&]' + name + '=([^&#]*)').exec(window.location.href);
        return results == null ? null : decodeURIComponent(results[1]) || 0;
      };

      // Decrypt and embed content
      function embedDecryptedContent() {
        var encryptedParam = $.urlParam('o'); // Get the encrypted 'o' parameter from the URL
        if (encryptedParam) {
          try {
            // Decrypt the parameter
            var decryptedHTML = aesCrypto.decrypt(convertstr(encryptedParam), convertstr('root'));
            if (decryptedHTML) {
              // Embed the decrypted content inside the 'btn btn-info' area
              $('#btngetlink').html(decryptedHTML).removeClass('d-none'); // Replace the button text with the decrypted HTML content
            } else {
              console.error('Decryption returned empty content.');
              alert('Failed to decrypt the content. Please check the URL parameter.');
            }
          } catch (error) {
            console.error('Decryption error:', error);
            alert('An error occurred while decrypting the content. Please try again.');
          }
        } else {
          console.error('Missing or invalid "o" parameter in the URL.');
          alert('The "o" parameter is missing or invalid in the URL.');
        }
      }

      // Automatically load and embed content
      embedDecryptedContent();
    });
    
